export CODE=day7.txt; time (runghc day7.hs | while read a b c d e; do OUTPUT=/tmp/output$a$b$c$d$e; echo 0 > $OUTPUT; (tail -F $OUTPUT | ./day7.awk <(echo $a) - | ./day7.awk <(echo $b) - | ./day7.awk <(echo $c) - | ./day7.awk <(echo $d) - | ./day7.awk <(echo $e) - >> $OUTPUT; echo "phases=$a $b $c $d $e $(tail -1 $OUTPUT)"; rm $OUTPUT) & done)|sort -k6 -g
