echo $'import Data.List\nmain = mapM_ putStrLn $ map (unwords . map show) $ permutations [0..4]' > day7.hs && runghc day7.hs | while read -a PHASES; do CODE=day7.txt; echo -n "phases=${PHASES[*]} "; awk -f day7.awk -v INPUT=<(echo ${PHASES[4]}; awk -f day7.awk -v INPUT=<(echo ${PHASES[3]}; awk -f day7.awk -v INPUT=<(echo ${PHASES[2]}; awk -f day7.awk -v INPUT=<(echo ${PHASES[1]}; awk -f day7.awk -v INPUT=<(echo ${PHASES[0]}; echo 0) $CODE) $CODE) $CODE) $CODE) $CODE; done|sort -g -k 6
